import { Component, HostListener, Renderer2, ElementRef, ViewChild } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  constructor(private renderer: Renderer2) { }
  @ViewChild('navbar') navbar: ElementRef | undefined;
  menuOpen = false;

  @HostListener('window:scroll', [])
  onWindowScroll() {
    if (this.navbar) {
      if (window.scrollY > 100) {
        this.renderer.addClass(this.navbar.nativeElement, 'sticky');
        this.renderer.addClass(this.navbar.nativeElement, 'shadow-sm');
      } else {
        this.renderer.removeClass(this.navbar.nativeElement, 'sticky');
        this.renderer.removeClass(this.navbar.nativeElement, 'shadow-sm');
      }
    }
  }

  toggleMenu() {
    this.menuOpen = !this.menuOpen;
  }
}
