import { InfoData } from 'src/app/ui/info-card/info-card.component';
import { Component } from '@angular/core';

@Component({
  selector: 'app-services-restorant',
  templateUrl: './services-restorant.component.html',
  styleUrls: ['./services-restorant.component.css']
})
export class ServicesRestorantComponent {
  // Dati da passare al componente figlio
  infoContainers: InfoData[] = [
    {
      icon: 'fa-utensils',
      title: 'Top Chefs',
      description: 'Our chefs are world-class professionals with a passion for excellence.'
    },
    {
      icon: 'fa-coffee',
      title: 'Premium Coffee',
      description: 'Enjoy freshly brewed coffee made with the finest beans.'
    },
    {
      icon: 'fa-wine-glass',
      title: 'Fine Wines',
      description: 'Discover our selection of exclusive fine wines.'
    }
  ];
}
